    #include<iostream>
    #include<vector>
    using namespace std;

    int dfs(vector<vector<int>>& grid, int i, int j) {
        if (i < 0 || i >= grid.size() || j < 0 || j >= grid[0].size() || grid[i][j] == 0) {
            return 0;
        }

        // 标记当前服务器已访问
        grid[i][j] = 0;
        int cnt = 1;

        // 向上、向下、向左、向右进行深度优先搜索
        cnt += dfs(grid, i - 1, j);
        cnt += dfs(grid, i + 1, j);
        cnt += dfs(grid, i, j - 1);
        cnt += dfs(grid, i, j + 1);
        return cnt;
    }

