    import java.util.HashMap;
    import java.util.Map;
    import java.util.Scanner;

    public class Main {
        static final long base = 13331;
        static final int maxLen = 10;

        static long calculateHash(String s) {
            long hash = 0;
            for (char c : s.toCharArray()) {
                hash = hash * base + c;
            }
            return hash;
        }

        public static void main(String[] args) {
            Scanner sc = new Scanner(System.in);

            int n = sc.nextInt();
            int q = sc.nextInt();

            sc.nextLine(); // Consume newline

            String s = sc.nextLine();

            long[] h = new long[n + 1];
            long[] p = new long[n + 1];

            h[0] = 0;
            p[0] = 1;

            for (int i = 1; i <= n; i++) {
                h[i] = h[i - 1] * base + s.charAt(i - 1);
                p[i] = p[i - 1] * base;
            }

            Map<Long, Integer> mp = new HashMap<>();

            for (int len = 1; len <= maxLen; len++) {
                for (int i = 0; i < n; i++) {
                    if (i + len > n) {
                        break;
                    }
                    long now = h[i + len] - h[i] * p[len];
                    mp.put(now, mp.getOrDefault(now, 0) + 1);
                }
            }

            for (int i = 0; i < q; i++) {
                String x = sc.nextLine();
                long y = calculateHash(x);

                if (mp.containsKey(y)) {
                    System.out.println(mp.get(y));
                } else {
                    System.out.println(0);
                }
            }
        }
    }



