    function fn(target, current){
        let cur = current.sort((a, b)=>{
            return a[0] < b[0] ? -1:1;
        });
        let min = cur[0][0]
        let max = cur[0][1];
        for(let i=1; i<cur.length; i++){
            if(min <= target[0] && max >= target[1]) return true;
            if(cur[i][0] <= max){
                max = cur[i][1] < max ? max : cur[i][1];
                continue;
            }else{
                return false;
            }
        }
        if(min <= target[0] && max >= target[1]) return true;
        else return false;
    }
