    public class Main {
        private static Map<Integer, Set<Integer>> lineMap = new HashMap<>();

        public static void main(String[] args) {
            Scanner sc = new Scanner(System.in);
            int n = sc.nextInt(), m = sc.nextInt(), s = sc.nextInt(), t = sc.nextInt();
            lineMap.clear();
            while (m-- > 0) {
                int x = sc.nextInt(), y = sc.nextInt();
                if (!lineMap.containsKey(x)) {
                    lineMap.put(x, new HashSet<>());
                }
                if (!lineMap.containsKey(y)) {
                    lineMap.put(y, new HashSet<>());
                }
                Set<Integer> xReach = lineMap.get(x);
                Set<Integer> yReach = lineMap.get(y);
                xReach.add(y);
                yReach.add(x);
            }
            Set<Integer> sCanReachSet = new HashSet<>();
            Set<Integer> tCanReachSet = new HashSet<>();
            int sGroupCount = reachCount(s, sCanReachSet);
            int tGroupCount = reachCount(t, tCanReachSet);
            // 可以直接到达的情况
            if (sCanReachSet.contains(t)) {
                // 排列组合的 C n 2
                System.out.println((n - 1) * (n) / 2);
            } else {
                System.out.println(sGroupCount * tGroupCount);
            }
        }

        // 获取从指定岛屿能到达的岛屿的个数（包含此岛屿）
        public static int reachCount(int n, Set<Integer> counted) {
            if (!lineMap.containsKey(n)) {
                return 1;
            }
            counted.add(n);
            Set<Integer> canReachSet = lineMap.get(n);
            for (Integer canReach : canReachSet) {
                if (counted.add(canReach)) {
                    reachCount(canReach, counted);
                }
            }
            return counted.size();
        }
    }
