    import java.util.Scanner;

    public class Main {
      public static void main(String[] args){
        Scanner scanner =new Scanner(System.in);
        int n= scanner.nextInt();// æ•°ç»„çš„å¤§å°
        int q= scanner.nextInt();// æ“ä½œæ¬¡æ•°
        long[] arr = new long[n];
        for(int i= 0;i <n; i++){
            arr[i]= scanner.nextLong();// è¾“å…¥æ•°ç»„å…ƒç´ 
        }
        Long mod = (long) 1000000007;// 10^9 + 7
        int[] countNotDoubled = new int[n];// è®°å½•æ¯ä¸ªå…ƒç´ æœªç¿»å€çš„æ¬¡æ•°
        for(int i= 0;i< q; i++){
            int xi = scanner.nextInt()- 1;// è¯»å–æ“ä½œçš„å…ƒç´ ï¼Œç´¢å¼•è°ƒæ•´ä¸ºä»0å¼€å§‹countNotDoubled[xi]++;//è®°å½•è¯¥å…ƒç´ æœªè¢«ç¿»å€çš„æ¬¡æ•°
            countNotDoubled[xi]+=1;
        }
        long sum = 0;
        for(int i= 0;i< n; i++){
          long doubleTimes =q- countNotDoubled[i];// è®¡ç®—å®é™…ç¿»å€çš„æ¬¡æ•°
          long addition =(arr[i]* powMod( 2,doubleTimes,mod))% mod;// å…ƒç´ ç»è¿‡ç¿»å€åçš„å€¼
          sum =(sum + addition)% mod;// ç´¯åŠ åˆ°æ€»å’Œä¸­
        }
        System.out.println(sum);
        }
      // å¿«é€Ÿå¹‚æ¨¡ç®—æ³•ï¼Œç”¨äºè®¡ç®—(base^exponent)% mod çš„å€¼1 usage
      public static long powMod(long base, long exponent, long mod) {
        long result = 1;
        base = base % mod;
        while(exponent>0){
            if(exponent %2 == 1)result =(result * base)% mod;exponent = exponent >> 1;
            base =(base * base)% mod;
        }
        return result;
      }
    }


