    public class Main {
        public static void main(String[] args) {
            Scanner in = new Scanner(System.in);
            long mod = 1_000_000_007L;
            while (in.hasNextInt()) {
                int n = in.nextInt();
                int q = in.nextInt();
                long a[] = new long[n];
                for(int i = 0; i < n; i++) {
                    a[i] = in.nextLong();
                }

                long normalTime = 1;
                Map<Integer, Integer> hashMap = new HashMap<>();
                Map<Integer, Long> mapTimes = new HashMap<>();
                for(int i = 0; i < q; i++) {
                    int op = in.nextInt();
                    hashMap.put(op - 1, hashMap.getOrDefault(op - 1, 0) + 1);
                    normalTime *= 2;
                    normalTime %= mod;
                    mapTimes.put(i + 1, normalTime);
                }

                long ans = 0;
                for(int i = 0; i < n; i++) {
                    Integer times = hashMap.get(i);
                    if(times == null) {
                        ans += (a[i] * normalTime) % mod;
                        ans %= mod;
                    } else {
                        long unNormalTime = mapTimes.get(q - times);
                        ans += (a[i] * unNormalTime) % mod;
                        ans %= mod;
                    }
                }
                System.out.println(ans);
            }
        }
    }

