    using namespace std;
    using ll = long long;
    const int N = (int)2e5+5;
    ll n;
    ll a[200005];
    ll tree[N << 3]; // å¼€å¤§ç‚¹æ²¡å…³ç³»
    ll diff[200005];
    // å°†æ•°å€¼xæ”¾å…¥çº¿æ®µæ ‘ï¼Œæ­¤æ—¶æ‰€åœ¨çš„åŒºé—´ä¸º[l,r]ï¼Œçº¿æ®µæ ‘ä¸‹æ ‡ä¸ºid
    void push(ll x,int l,int r,int id){
        if(l >= r){
            tree[id]++;
            return;
        }
        int mid = (l+r)>>1;
        if(x <= mid){
            push(x,l,mid,id<<1);
        }
        else{
            push(x,mid+1,r,(id<<1)+1);
        }
        tree[id] = tree[id<<1] + tree[(id<<1)+1];
    }
    // æ±‚<xçš„diffæ•°é‡
    ll getsum(ll x,int l,int r,int id){
        if(l >= r){
            if(x <= l) return 0;
            return tree[id];
        }
        ll res = 0;
        int mid = (l+r)>>1;
        if(x > mid){
            res += tree[id<<1];
            res += getsum(x,mid+1,r,(id<<1)+1);
        }
        else{
            res += getsum(x,l,mid,(id<<1));
        }
        return res;
    }
    void solve(){
        ll m = 0;
        for(int i = 1;i <= n;++i){
            cin >> a[i]; // 1 <= a[i] <= 2
            diff[i] = diff[i-1] + (a[i] == 2 ? 1 : -1);
        }
        for(int i = 0;i <= n;++i){
            diff[i] += n+1; // ä¿è¯æ‰€æœ‰æ•°ä¸ºæ­£æ•°
        }
        push(diff[0],0,2*n+1,1); // ç”±äºl-1å¯ä»¥ä¸º0ï¼Œå› æ­¤å…ˆæŠŠdiff[0]æ”¾è¿›å»
        for(int i = 1;i <= n;++i){
            m += getsum(diff[i],0,2*n+1,1); // ä»æœ€å¤§çš„åŒºé—´[0,2n+1]å¼€å§‹é€’å½’æŸ¥è¯¢
            push(diff[i],0,2*n+1,1); // å•ç‚¹ä¿®æ”¹ï¼Œå…¶æ‰€åœ¨çš„åŒºé—´çš„å€¼éƒ½è¦+1
        }
        ll p = n*(n+1)/2 - m;
        ll ans = m*2 + p;
        cout << ans << "\n";
    }
    int main() {
        ios::sync_with_stdio(false);
        cin.tie(nullptr);
        cout.tie(nullptr);
        while(cin >> n){
            solve();
        }
        return 0;
    }

